let [, initial, sign, number] = matches;
let mult = 1;
if (sign === "-") {
    mult = -1;
}
let text;
if (!isNaN(Number(number))) {
    text = `1d20+${mult * Number(number)}`;
}
return [original, " (", { text }, ")"];

